"use client";

import { useState, useEffect } from 'react';

const Win98_theme_manager = ({ onClose }) => {
  const [selected_theme, setSelectedTheme] = useState('classic');
  const [is_dragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 100, y: 100 });
  const [drag_offset, setDragOffset] = useState({ x: 0, y: 0 });  const themes = {
    classic: 
    {
      name: 'Windows 98 Classic',
      colors: 
      {
        primary: '#008080',
        secondary: '#c0c0c0',
        accent: '#000080',
        window: '#c0c0c0',
        text: '#000000',
        highlight: '#0000ff'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: 'üñ•Ô∏è'
    },
    
    forest: 
    {
      name: 'Forest Green',
      colors: 
      {
        primary: '#228B22',
        secondary: '#90EE90',
        accent: '#006400',
        window: '#F0FFF0',
        text: '#000000',
        highlight: '#32CD32'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: 'üå≤'
    },

    ocean: 
    {
      name: 'Ocean Blue',
      colors: 
      {
        primary: '#006994',
        secondary: '#87CEEB',
        accent: '#4682B4',
        window: '#F0F8FF',
        text: '#000000',
        highlight: '#1E90FF'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: 'üåä'
    },

    sunset: 
    {
      name: 'Sunset Orange',
      colors: 
      {
        primary: '#FF6347',
        secondary: '#FFE4B5',
        accent: '#FF4500',
        window: '#FFF8DC',
        text: '#000000',
        highlight: '#FF8C00'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: '‚òÄÔ∏è'
    },

    purple: 
    {
      name: 'Royal Purple',
      colors: 
      {
        primary: '#663399',
        secondary: '#DDA0DD',
        accent: '#4B0082',
        window: '#F8F0FF',
        text: '#000000',
        highlight: '#9370DB'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: 'üëë'
    },

    matrix: 
    {
      name: 'Matrix Green',
      colors: 
      {
        primary: '#000000',
        secondary: '#001100',
        accent: '#00ff00',
        window: '#001100',
        text: '#00ff00',
        highlight: '#00ff00'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: 'üü¢'
    },

    cyber: 
    {
      name: 'Cyberpunk',
      colors: 
      {
        primary: '#0a0a0a',
        secondary: '#1a0033',
        accent: '#ff00ff',
        window: '#1a0033',
        text: '#00ffff',
        highlight: '#ff00ff'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: 'ü§ñ'
    },

    retro: 
    {
      name: 'Retro Pink',
      colors: 
      {
        primary: '#ff1493',
        secondary: '#ffb6c1',
        accent: '#dc143c',
        window: '#fff0f5',
        text: '#000000',
        highlight: '#ff69b4'
      },
      icon: 'data:image/svg+xml;base64,',
      fallback: 'üíñ'
    }
  };

  useEffect(() => {
    const saved_theme = localStorage.getItem('win98-theme') || 'classic';
    setSelectedTheme(saved_theme);
    apply_theme(themes[saved_theme]);
  }, []);

  const apply_theme = (theme) => {
    const root = document.documentElement;
    root.style.setProperty('--win98-primary', theme.colors.primary);
    root.style.setProperty('--win98-secondary', theme.colors.secondary);
    root.style.setProperty('--win98-accent', theme.colors.accent);
    root.style.setProperty('--win98-window', theme.colors.window);
    root.style.setProperty('--win98-text', theme.colors.text);
    root.style.setProperty('--win98-highlight', theme.colors.highlight);
  };

  const handle_theme_select = (theme_key) => {
    setSelectedTheme(theme_key);
    localStorage.setItem('win98-theme', theme_key);
    apply_theme(themes[theme_key]);
  };

  const handle_mouse_down = (e) => {
    if(e.target.closest('button') || e.target.closest('.theme-item')) return;
    
    setIsDragging(true);
    setDragOffset({
      x: e.clientX - position.x,
      y: e.clientY - position.y
    });
  };

  const handle_mouse_move = (e) => {
    if(!is_dragging) return;
    
    setPosition({
      x: e.clientX - drag_offset.x,
      y: e.clientY - drag_offset.y
    });
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  useEffect(() => {
    if(is_dragging) 
      {
      document.addEventListener('mousemove', handle_mouse_move);
      document.addEventListener('mouseup', handleMouseUp);
      
      return () => {
        document.removeEventListener('mousemove', handle_mouse_move);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [is_dragging, drag_offset]);

  return (
    <div 
      className = "window theme-manager-window"
      style = {{
        position: 'absolute',
        left: `${position.x}px`,
        top: `${position.y}px`,
        width: '600px',
        zIndex: 10000,
        cursor: is_dragging ? 'grabbing' : 'default'
      }}
    >
      {/* Title Bar */}
      <div 
        className = "title-bar"
        onMouseDown = {handle_mouse_down}
        style = {{ cursor: is_dragging ? 'grabbing' : 'grab' }}
      >
        <div className = "title-bar-text">
          <img 
            src = "./icons/win98/png/themes-0.png" 
            alt = "Themes"
            style = {{
              width: '16px',
              height: '16px',
              imageRendering: 'pixelated',
              marginRight: '4px',
              verticalAlign: 'middle'
            }}
            onError = {(e) => {
              e.target.style.display = 'none';
              e.target.insertAdjacentHTML('afterend', '<span>üé®</span>');
            }}
          />
          Theme Manager
        </div>
        <div className = "title-bar-controls">
          <button aria-label = "Close" onClick = {onClose}></button>
        </div>
      </div>

      {/* Window Body */}
      <div className = "window-body" style = {{ padding: '15px' }}>
        <fieldset style = {{ marginBottom: '15px' }}>
          <legend>
            <img 
              src = "./icons/win98/png/color_profile-0.png"
              alt = "Colors"
              style = {{
                width: '16px',
                height: '16px',
                imageRendering: 'pixelated',
                marginRight: '4px',
                verticalAlign: 'middle'
              }}
              onError = {(e) => {
                e.target.style.display = 'none';
                e.target.insertAdjacentHTML('afterend', '<span>üé®</span>');
              }}
            />
            <span style = {{ display: 'none' }}>üé®</span>
            Choose Your Theme
          </legend>
          
          <div style = {{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
            gap: '10px',
            padding: '10px 0'
          }}>
            {Object.entries(themes).map(([key, theme]) => (
              <div
                key = {key}
                className = {`theme-item ${selected_theme === key ? 'selected' : ''}`}
                onClick = {() => handle_theme_select(key)}
                style = {{
                  border: selected_theme === key ? '2px solid #000080' : '1px solid #808080',
                  borderRadius: '4px',
                  padding: '10px',
                  cursor: 'pointer',
                  background: selected_theme === key ? '#e0e0e0' : '#f0f0f0',
                  textAlign: 'center',
                  transition: 'all 0.2s'
                }}
              >
                <div style = {{ marginBottom: '5px', display: 'flex', justifyContent: 'center', alignItems: 'center', height: '32px' }}>
                  <img 
                    src = {theme.icon}
                    alt = {theme.name}
                    style = {{
                      width: '32px',
                      height: '32px',
                      imageRendering: 'pixelated'
                    }}
                    onError = {(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'block';
                    }}
                  />
                  <div style = {{ display: 'none', fontSize: '24px' }}>
                    {typeof theme.fallback === 'string' && theme.fallback.startsWith('data:') ? (
                      <img 
                        src = {theme.fallback}
                        alt = {theme.name}
                        style = {{
                          width: '32px',
                          height: '32px',
                          imageRendering: 'pixelated'
                        }}
                      />
                    ) : (
                      theme.fallback
                    )}
                  </div>
                </div>
                <div style = {{ fontSize: '12px', fontWeight: 'bold', marginBottom: '5px' }}>
                  {theme.name}
                </div>
                
                {/* Color Preview */}
                <div style = {{ 
                  display: 'flex', 
                  justifyContent: 'center', 
                  gap: '2px', 
                  marginTop: '8px' 
                }}>
                  <div style = {{ 
                    width: '16px', 
                    height: '16px', 
                    backgroundColor: theme.colors.primary,
                    border: '1px solid #666',
                    borderRadius: '2px'
                  }} />
                  <div style = {{ 
                    width: '16px', 
                    height: '16px', 
                    backgroundColor: theme.colors.accent,
                    border: '1px solid #666',
                    borderRadius: '2px'
                  }} />
                  <div style = {{ 
                    width: '16px', 
                    height: '16px', 
                    backgroundColor: theme.colors.highlight,
                    border: '1px solid #666',
                    borderRadius: '2px'
                  }} />
                </div>
              </div>
            ))}
          </div>
        </fieldset>

        <fieldset style = {{ marginBottom: '15px' }}>
          <legend>
            <img 
              src = "./icons/win98/png/notepad_file-0.png"
              alt = "Info"
              style = {{
                width: '16px',
                height: '16px',
                imageRendering: 'pixelated',
                marginRight: '4px',
                verticalAlign: 'middle'
              }}
              onError = {(e) => {
                e.target.style.display = 'none';
                e.target.insertAdjacentHTML('afterend', '<span>üìã</span>');
              }}
            />
            <span style = {{ display: 'none' }}>üìã</span>
            Theme Info
          </legend>
          <div style = {{ fontSize: '11px', padding: '8px' }}>
            <div><strong>Current Theme:</strong> {themes[selected_theme].name}</div>
            <div style = {{ marginTop: '5px' }}>
              <strong>Colors:</strong>
              <div style = {{ marginLeft: '10px', marginTop: '3px' }}>
                <div>Primary: {themes[selected_theme].colors.primary}</div>
                <div>Accent: {themes[selected_theme].colors.accent}</div>
                <div>Highlight: {themes[selected_theme].colors.highlight}</div>
              </div>
            </div>
          </div>
        </fieldset>

        {/* Action Buttons */}
        <div style = {{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginTop: '15px'
        }}>
          <button 
            onClick = {() => handle_theme_select('classic')}
            style = {{ minWidth: '100px', display: 'flex', alignItems: 'center', gap: '4px', justifyContent: 'center' }}
          >
            <img 
              src = "data:image/svg+xml;base64,"
              alt = "Reset"
              style = {{
                width: '16px',
                height: '16px',
                imageRendering: 'pixelated'
              }}
              onError = {(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'inline';
              }}
            />
            <span style = {{ display: 'none' }}>üîÑ</span>
            Reset
          </button>
          
          <div style = {{ display: 'flex', gap: '8px' }}>
            <button 
              onClick = {() => {
                const random_themes = Object.keys(themes);
                const random_theme = random_themes[Math.floor(Math.random() * random_themes.length)];
                handle_theme_select(random_theme);
              }}
              style = {{ minWidth: '80px', display: 'flex', alignItems: 'center', gap: '4px', justifyContent: 'center' }}
            >
              <img 
                src = "./icons/win98/png/gears-0.png"
                alt = "Random"
                style = {{
                  width: '16px',
                  height: '16px',
                  imageRendering: 'pixelated'
                }}
                onError = {(e) => {
                  e.target.style.display = 'none';
                  e.target.insertAdjacentHTML('afterend', '<span>üé≤</span>');
                }}
              />
              <span style = {{ display: 'none' }}>üé≤</span>
              Random
            </button>
            
            <button 
              onClick = {onClose}
              style = {{ 
                minWidth: '80px',
                backgroundColor: '#ff6b6b',
                color: 'white',
                fontWeight: 'bold',
                display: 'flex',
                alignItems: 'center',
                gap: '4px',
                justifyContent: 'center'
              }}
            >
              <img 
                src = "./icons/win98/png/check-0.png"
                alt = "Apply"
                style = {{
                  width: '16px',
                  height: '16px',
                  imageRendering: 'pixelated',
                  filter: 'brightness(0) invert(1)'
                }}
                onError = {(e) => {
                  e.target.style.display = 'none';
                  e.target.insertAdjacentHTML('afterend', '<span>‚úÖ</span>');
                }}
              />
              <span style = {{ display: 'none' }}>‚úì</span>
              Apply
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Win98_theme_manager;
